import { func } from "prop-types";
import React, { useState } from "react";
import ReactDOM from "react-dom";
import {Routes, Route, Link, useNavigate} from 'react-router-dom'
import './BuildingForm.css';
import './form.css'
import checkbox from './checkbox.js'
import input from './input.js'
import choice from './dropdown'
import BuildingList from "./BuildingList";

function Main (){
  const navigate = useNavigate();
  let province = 
  [
    {value: 1, text: "Hà Nội"},
    {value: 2, text: "Thành phố Hồ Chí Minh"},
    {value: 3, text: "Đà Nẵng"}
  ]
  let district = 
  [
    {value: 1, text: "1"},
    {value: 2, text: "2"},
    {value: 3, text: "3"}
  ]
  let communte = 
  [
    {value: 1, text: "1"},
    {value: 2, text: "2"},
    {value: 3, text: "3"}
  ]
  let budget = [
    {value: "electric", text: "Điện"},
    {value: "wifi", text: "wifi"},
    {value: "water", text: "Nước"},
    {value: "park", text: "Giữ xe"}
  ]
  const handleClick = (event) => {
    event.preventDefault();
    navigate('/BuildingList');
  }
  const form = (
    <div>
      <span className="function-title textxlsemibold">Thêm toà nhà</span>
      <form onSubmit={handleClick} className="main-zone">
        <div className="left-col">
        <div className="aitem-area">
          {input(2,"building_name","Tên","Tên toà nhà")}
        </div>
        <div className="item-area double-col">
          {input(1,"floor","Nhập số tầng","Số tầng")}<div/>
        </div>
        <div className="item-area">
          <span className="form-subtitle textmdsemibold">Dịch vụ</span>
          <div className="checkbox-area">
            {budget.map((x) => checkbox("budget",x.value,x.text))}
          </div>
        </div>
        <div className="item-area">
          <span className="form-subtitle textmdsemibold">Mô tả</span>
          <textarea name="description" className="text-input" placeholder="Mô tả"></textarea>
        </div>
        </div>
        <div className="right-col">
        <div className="item-area">
          {choice("Tỉnh/Thành",province)}
        </div>
        <div className="item-area double-col">
            {choice("Quận/Huyện",district)}
            {choice("Phường/Xã",communte)}
        </div>
        <div className="item-area">
          {input(2,"num_street","Tên","Số, đường")}
        </div>
        <div className="item-area">
          <span className="form-subtitle textmdsemibold">Thêm ảnh</span>
            <input type="file" name="myfile" id="myfile" hidden/>
            <label for="myfile" className="add-building-screen-button">Nhấn để thêm ảnh</label>
        </div>
        </div>
        <button type="submit" className="add-building-screen-small-secondary-button">
          <span className="add-building-screen-text28 textsmsemibold">Thêm toà nhà</span>
        </button>
      </form>
    </div>
  )
  return (
    <div>{form}</div>
  )
}

export default function BuildingForm ()
{
  return(
    <div>
    <Routes>
      <Route path="/" element={<Main />} />
      <Route path="/BuildingList" element={<BuildingList />} />
    </Routes>
    </div>
  )
}
