import axios from 'axios';
import React, { useState } from "react";
import {Routes, Route, Link, useNavigate} from 'react-router-dom';
import '../style/form.css';
import {input,dropdown,choice} from "../style/JSfunc";
import EmployeeList from "./EmployeeList";

let employeeID = 0;

export const setEmployeeID = (id) => 
{
  employeeID = id
  console.log(employeeID)
}

const roles = [
  {code: 1, name: "Sửa chữa"},
  {code: 2, name: "Vệ sinh"},
  {code: 3, name: "Sửa chữa"}
]
const positions = [
  {code: 1, name: "Sửa chữa"},
  {code: 2, name: "Vệ sinh"},
  {code: 3, name: "Sửa chữa"}
]
const workdays =
[
  {code: 2, name: "Thứ hai"},
  {code: 3, name: "Thứ ba"},
  {code: 4, name: "Thứ tư"},
  {code: 5, name: "Thứ năm"},
  {code: 6, name: "Thứ sáu"},
  {code: 7, name: "Thứ bảy"},
  {code: 1, name: "Chủ nhật"}
]
const genders = [
  {code: 0, name: "Nam"},
  {code: 1, name: "Nữ"},
]
const shifts = [
  {code: 1, name: "Ca 1"},
  {code: 2, name: "Ca 2"},
  {code: 3, name: "Ca 3"}
]
function Main (){
  const [employee, loadEmployee]= useState();
  const [daystart, setDayStart]= useState();
  const [dayend, setDayEnd]= useState();

  useEffect( ()=>{
    loadItem('employees', employeeID, loadEmployee);
  },[employeeID]);

  const Building_Name = (employee!=null) ? employee.attributes.Building_Name : null
  const NumFloor = (employee!=null) ? employee.attributes.Num_of_Floors : null
  const Description = (employee!=null) ? employee.attributes.Description : null

  const navigate = useNavigate();
  const createNewEmployee = async (data) => {
    await axios
    .post("http://localhost:1337/api/employees", data)
  }
  const handleClick = (event) => {
    event.preventDefault();
    let {Lastname, Firstname, Email, Password, Sex, Phone, Role, Salary, Benefit,offday,Shift} = document.forms[0];
    const data = {
      'data': {
        "Lastname": Lastname.value,
        "Firstname": Firstname.value,
        "Email": Email.value,
        "Password": Password.value,
        "Sex": Sex.value,
        "Phone": Phone.value,
        "Role": Role.value,
        "Salary": Salary.value,
        "Benefit": Benefit.value,
        "Max_Leave_Day": offday.value,
        "Shift": Shift.value,
      }
    }
    createNewEmployee(data);
    navigate('/Employee/EmployeeList');
  }
  return(
    <div>
      <span className="function-title textxlsemibold">{(employeeID==0) ? "Thêm nhân viên" : `Chỉnh sửa nhân viên EMP ${employeeID}`}</span>
      <form onSubmit={handleClick} className="main-zone">
        <div className="big-row">
          <div className="col-left">
            <div className="item-area">
              {input(1,"Họ lót","Lastname",true,"Tên",null,null,1,20,"")}
            </div>
          </div>
          <div className="col-left">
            <div className="item-area">
              {input(1,"Tên","Firstname",true,"Tên",null,null,1,15,"")}
            </div>
          </div>
        </div> 
        <div className="big-row">
          <div className="col-left">
            <div className="item-area">
              {input(1,"Email","Email",true,"Email",null,null,1,60,"")}
            </div>
          </div>
          <div className="col-left">
            <div className="item-area">
              {input(1,"Mật khẩu","Password",true,"Mật khẩu đăng nhập lần đầu",null,null,1,60,"")}
            </div>
          </div>
        </div> 
        <div className="big-row">
          <div className="col-left">
            <div className="item-area">
              {choice("Giới tính","Sex",genders)}
            </div>
          </div>
          <div className="col-left">
            <div className="item-area double-col">
              {input(2,"Ngày sinh","birthday",true,"",null,null,"","","")}
              {input(4,"Số điện thoại","Phone",true,"0123456789",null,null,"","","0([3-5]|[7-9])[0-9]{8}")}
            </div>
          </div>
        </div> 
        <div className="big-row">
          <div className="col-left">
            <div className="item-area">
              <span className="form-subtitle textmdsemibold">Công việc</span>
              <div className="item-area double-col">
                {dropdown(1, "Chức vụ","Role",true, roles, "",
                null, null, false, "Chọn chức vụ","Chọn chức vụ")}
                {dropdown(1, "Vị trí","position",true, positions, "",
                null, null, false, "Chọn vị trí","Chọn vị trí")}
              </div>
              <div className="item-area double-col">
                {input(0,"Lương (triệu đồng)","Salary",true,"Đơn vị: triệu đồng",null,null,1,"",0.01)}
                {input(0,"Phúc lợi","Benefit",true,"Nhập số tiền",null,null,0,"",100)}
              </div>
            </div>
          </div>
          <div className="col-left">
          <span className="form-subtitle textmdsemibold">Thời gian làm việc</span>
          <div className="item-area double-col">
            {dropdown(1,"Từ","work-start",true,workdays,"",
            daystart,setDayStart,false,"Chọn ngày","Chọn ngày")}
            {dropdown(1, "Đến","work-end",true,workdays,"",
            dayend,setDayEnd,false,"Chọn ngày","Chọn ngày")}
          </div>
          <div className="item-area double-col">
            {input(0,"Ngày nghỉ tối đa","offday","Ngày nghỉ",null,null,0,100,1)}<div/>
          </div>
          <div className="item-area">
            {choice("Ca làm việc","Shift",shifts)}
          </div>
          </div>
        </div>
        <div className="submit-area">
          <button type="submit" className="submit-button">
            <span className="submit-btntxt textsmsemibold">{(employeeID==0) ? "Thêm nhân viên" : "Cập nhật"}</span>
          </button>
        </div>
      </form>
    </div>
  )
}

export default function EmployeeForm ()
{
  return(
    <Routes>
      <Route path="/" element={<Main />} />
      <Route path="/Employee/EmployeeList" element={<EmployeeList />} />
    </Routes>
  )
}
