import { func } from "prop-types";
import React, { useState } from "react";
import {Routes, Route, Link, useNavigate} from 'react-router-dom';
import '../style/form.css';
import {checkbox,input,dropdown,choice} from "../style/JSfunc";
import EmployeeList from "./EmployeeList";

const roles = [
  {code: 1, name: "Sửa chữa"},
  {code: 2, name: "Vệ sinh"},
  {code: 3, name: "Sửa chữa"}
]
const positions = [
  {code: 1, name: "Sửa chữa"},
  {code: 2, name: "Vệ sinh"},
  {code: 3, name: "Sửa chữa"}
]
const workdays =
[
  {code: 2, name: "Thứ hai"},
  {code: 3, name: "Thứ ba"},
  {code: 4, name: "Thứ tư"},
  {code: 5, name: "Thứ năm"},
  {code: 6, name: "Thứ sáu"},
  {code: 7, name: "Thứ bảy"},
  {code: 1, name: "Chủ nhật"}
]
const genders = [
  {code: 0, name: "Nam"},
  {code: 1, name: "Nữ"},
]
const shifts = [
  {code: 1, name: "Ca 1"},
  {code: 2, name: "Ca 2"},
  {code: 3, name: "Ca 3"}
]
function Main (){
  const [daystart, setDayStart]= useState();
  const [dayend, setDayEnd]= useState();
  const handleDayStart =(event)=>{
    setDayStart(event.target.value);
    event.preventDefault();
  }

  const navigate = useNavigate();
  const handleClick = (event) => {
    event.preventDefault();
    navigate('/Employee/EmployeeList');
  }
  return(
    <div>
      <span className="function-title textxlsemibold">Thêm nhân viên</span>
      <form onSubmit={handleClick} className="main-zone">
        <div className="big-row">
          <div className="col-left">
            <div className="item-area">
              {input(1,"Họ lót","surname","Tên",null,null,1,20,"")}
            </div>
          </div>
          <div className="col-left">
            <div className="item-area">
              {input(1,"Tên","firstname","Tên",null,null,1,15,"")}
            </div>
          </div>
        </div> 
        <div className="big-row">
          <div className="col-left">
            <div className="item-area">
              {input(1,"Email","email","Email",null,null,1,60,"")}
            </div>
          </div>
          <div className="col-left">
            <div className="item-area">
              {input(1,"Mật khẩu","password","Mật khẩu đăng nhập lần đầu",null,null,1,60,"")}
            </div>
          </div>
        </div> 
        <div className="big-row">
          <div className="col-left">
            <div className="item-area">
              {choice("Giới tính","gender",genders)}
            </div>
          </div>
          <div className="col-left">
            <div className="item-area double-col">
              {input(2,"Ngày sinh","birthday","",null,null,"","","")}
              {input(4,"Số điện thoại","Phone","0123456789",null,null,"","","0([3-5]|[7-9])[0-9]{8}")}
            </div>
          </div>
        </div> 
        <div className="big-row">
          <div className="col-left">
            <div className="item-area">
              <span className="form-subtitle textmdsemibold">Công việc</span>
              <div className="item-area double-col">
                {dropdown(1, "Chức vụ","role",true, roles, "",
                null, null, false, "Chọn chức vụ","Chọn chức vụ")}
                {dropdown(1, "Vị trí","position",true, positions, "",
                null, null, false, "Chọn vị trí","Chọn vị trí")}
              </div>
              <div className="item-area double-col">
                {input(0,"Lương (triệu đồng)","salary","Đơn vị: triệu đồng",null,null,1,"",0.01)}
                {input(0,"Phúc lợi","welfare","Nhập số tiền",null,null,0,"",100)}
              </div>
            </div>
          </div>
          <div className="col-left">
          <span className="form-subtitle textmdsemibold">Thời gian làm việc</span>
          <div className="item-area double-col">
              {dropdown("Từ","work-start",setDayStart,false,workdays,"day",daystart, "Chọn ngày","Chọn ngày")}
              {dropdown("Đến","work-end",setDayEnd,false,workdays,"day",dayend, "Chọn ngày","Chọn ngày")}
          </div>
          <div className="item-area double-col">
            {input(0,"Ngày nghỉ tối đa","offday","Ngày nghỉ",null,null,0,100,1)}<div/>
          </div>
          <div className="item-area">
            {choice("Ca làm việc","shift",shifts)}
          </div>
          </div>
        </div>
        <div className="submit-area">
          <button type="submit" className="submit-button">
            <span className="submit-btntxt textsmsemibold">Thêm nhân viên</span>
          </button>
        </div>
      </form>
    </div>
  )
}

export default function EmployeeForm ()
{
  return(
    <Routes>
      <Route path="/" element={<Main />} />
      <Route path="/Employee/EmployeeList" element={<EmployeeList />} />
    </Routes>
  )
}
