import { func } from "prop-types";
import React, { useState } from "react";
import ReactDOM from "react-dom";
import {Routes, Route, Link, useNavigate} from 'react-router-dom'
import './VoucherForm.css';
import './form.css'
import input from './input.js'
import VoucherList from "./VoucherList";

function Main (){
  const navigate = useNavigate();
  const handleClick = (event) => {
    event.preventDefault();
    navigate('/VoucherList');
  }
    return(
      <div>
        <span className="add-voucher-screen-text textxlsemibold">Thêm mã giảm giá</span>
        <form className="add-voucher-screen-list" onSubmit={handleClick}>
          <div className="add-voucher-screen-name">
            {input(2,"vouchername","Tên","Tên mã")}
          </div>
          <div className="add-voucher-screen-row">
            <div className="add-voucher-screen-floors">
              {input(3,"startday","","Ngày bắt đầu")}
            </div>
            <div className="add-voucher-screen-floors1">
              {input(3,"endday","","Ngày kết thúc")}
            </div>
            <div className="add-voucher-screen-floors2">
              {input(4,"starttime","","Thời gian bắt đầu")}
            </div>
            <div className="add-voucher-screen-floors3">
              {input(4,"endtime","","Thời gian kết thú")}
            </div>
          </div>
          <button className="add-voucher-screen-small-secondary-button">
            <span className="add-voucher-screen-text020 textsmsemibold">Thêm mã</span>
          </button>
          <div className="add-voucher-screen-row01">
              <div className="add-voucher-screen-apply">
                <div className="add-voucher-screen-radiobutton">
                    <input type="radio" id="1" name="applyfor" value="1"/>
                    <label for="1">Tất cả các căn hộ</label>
                    <input type="radio" id="2" name="applyfor" value="2"/>
                    <label for="2">Một vài căn hộ</label>
                </div>
                <span className="add-voucher-screen-text026 textmdsemibold">Các căn hộ áp dụng</span>
              </div>
            </div>
            <div className="add-voucher-screen-row02">
              <div className="add-voucher-screen-apply1">
                <span className="form-subtitle textmdsemibold">Áp dụng cho</span>
                <div className="add-voucher-screen-radiobutton2">
                    <input type="radio" id="1" name="applyfor" value="1"/>
                    <label for="1">Tất cả khách hàng</label>
                    <input type="radio" id="2" name="applyfor" value="2"/>
                    <label for="2">Khách hàng mới</label>
                </div>
              </div>
            </div>
            <div className="add-voucher-screen-row03">
              <div className="add-voucher-screen-row04">
                {input(1,"quantity","Số lượng","Số lượng")}
              </div>
              <div className="add-voucher-screen-floors5">
                {input(1,"percent_off","Phần trăm","Phần trăm giảm")}
              </div>
              <div className="add-voucher-screen-floors6">
                {input(1,"max_off","Tối đa","Giảm tối đa")}
              </div>
            </div>
        </form>
      </div> 
    )
}

export default function VoucherForm ()
{
  return(
    <div>
    <Routes>
      <Route path="/" element={<Main />} />
      <Route path="/VoucherList" element={<VoucherList />} />
    </Routes>
    </div>
  )
}
