import React from "react";
import "./form.css";

const input = (type, name, subtile, title) => 
{
  return(
    <span><p className="form-subtitle textmdsemibold">{title}</p>
    <input
      name={name}
      type={(type==1) ? "number" : (type==2) ? "text" : (type==3) ? "date" : "time"}
      min={(type==1) ? "0" : ""}
      placeholder={subtile}
      className={(type==2) ? "form-input" : "form-input-half"} />
    </span>
  )
}

export default input
