import { func } from "prop-types";
import React, { useState } from "react";
import ReactDOM from "react-dom";
import './main.css';
import BuildingList from './buildinglist.js';
import ApartmentList from './apartmentlist.js';
import ContractList from './contractlist.js';
import CustomerList from './customerlist.js';
import VoucherList from './voucherlist.js';
import EmployeeList from './employeelist.js';
import CheckAttendanceList from './checkattendancelist.js';
import CreateQRScreen from './QRcode.js';
import BuildingForm from './buildingform.js';
import ApartmentForm from './apartmentform.js';

let nav_lst=
[
  {name: "Thống kê", expand: 0, select: 0},
  {name: "Tòa nhà", expand: 0, select: 0},
  {name: "Căn hộ", expand: 1, select: 0,
    subnav:
    [
      {name: "Một hợp đồng", select: 1},
      {name: "Nhiều hợp đồng", select: 0},
    ],
    cur: 0  
  },
  {name: "Hợp đồng", expand: 0, select: 0},
  {name: "Hóa đơn", expand: 0, select: 0},
  {name: "Mã giảm giá", expand: 0, select: 0},
  {name: "Khách hàng", expand: 0, select: 0},
  {name: "Nhân viên", expand: 0, select: 0},
  {name: "Yêu cầu", expand: 0, select: 0},
  {name: "Điểm danh nhân viên", expand: 1, select: 0,
    subnav:
    [
      {name: "Điểm danh", select: 1},
      {name: "Danh sách điểm danh", select: 0},
    ],
    cur: 0  
  },
]

const Left_logo = () =>
{
    return (
      <div>
          <div className="buildinglist-screen-logo">
            <span className="buildinglist-screen-text06 displaysmsemibold">Appname</span>
            <img
              alt="Line12I414"
              src="/playground_assets/line12i414-v2in.svg"
              className="buildinglist-screen-line12"
            />
          </div>
          <div className="buildinglist-screen-profile1">
            <div className="buildinglist-screen-info1">
              <img
                alt="AvatarI414"
                src="/playground_assets/avatari414-twdi.svg"
                className="buildinglist-screen-avatar1"
              />
              <span className="buildinglist-screen-text08 textmdsemibold">Nguyen Van B</span>
              <span className="buildinglist-screen-text10 textsmsemibold">Admin</span>
            </div>
          </div>
      </div>
    )
}
function Sub_Nav (props){
  if (props.parentselect==1)
  return (
    <div className="nav-elmt-bg" onClick={props.onClick}>
      <span className="nav-elmt-text" style={{color: props.select ? "rgba(46, 144, 250, 1)" : ""}}>{props.name}</span>
      <img
        src={`/Nav-Logo/${props.name}.svg`}
        className="nav-elmt-img"
        style={{fill: props.select ? "rgba(46, 144, 250, 1)" : ""}}
      />
    </div>
  );
}

function Nav_Elmt (props) {
  const expand = (i) =>
  {
    if (i==1)
    return(
      <img
      src="/playground_assets/anglelefti414-d6w7.svg"
      className="angleleft"
      />
    )
  }
  return (
    <div className="nav-elmt-bg" onClick={props.onClick}>
      <span className="nav-elmt-text" style={{color: props.select ? "rgba(46, 144, 250, 1)" : ""}}>{props.name}</span>
      <img
        src={`/Nav-Logo/${props.name}.svg`}
        className="nav-elmt-img"
        style={{fill: props.select ? "rgba(46, 144, 250, 1)" : ""}}
      />
      {expand(props.expand)}
    </div>
  );
}

class Content extends React.Component
{
  constructor(props) {
    super(props);
    this.state = {
        nav: nav_lst,
        pre: 0
    };
  }

  handleClick(i) {
    const nav = this.state.nav.slice();
    nav[i].select = 1;
    nav[this.state.pre].select = 0;
    this.setState({nav: nav, pre: i});
  }

  handleClicksub(i,j) {
    const nav = this.state.nav.slice();
    nav[i].subnav[j].select = 1;
    nav[i].subnav[1-j].select = 0;
    this.setState({nav: nav});
  }
  render_main()
  {
    if (this.state.pre==1) return(<BuildingList />)
    else if (this.state.pre==2) return(<ApartmentList />)
    else if (this.state.pre==3) return(<ContractList />)
    else if (this.state.pre==5) return(<VoucherList />)
    else if (this.state.pre==6) return(<CustomerList />)
    else if (this.state.pre==7) return(<EmployeeList />)
    else if (this.state.pre==9) return(<CreateQRScreen />)
    else if (this.state.pre==10) return(<CheckAttendanceList />)
  }
  render_subnav(i,j)
  {
    return(
      <Sub_Nav
        parentselect={this.state.nav[i].select}
        select={this.state.nav[i].subnav[j].select}
        name={this.state.nav[i].subnav[j].name}
        onClick={() => this.handleClicksub(i,j)}
      />
    )
  }
  render_nav(i)
  {
    return(
      <Nav_Elmt
        select={this.state.nav[i].select}
        name={this.state.nav[i].name}
        expand={this.state.nav[i].expand}
        onClick={() => this.handleClick(i)}
      />
    )
  }
  render()
  {
    return (
    <div>
    <div className="buildinglist-screen-navigator">
      {Left_logo()}
      <div className="thongke">{this.render_nav(0)}</div>      
      <span className="buildinglist-screen-text14 textmdsemibold">Quản lý</span>
      <div className="navigator-list">
      {this.render_nav(1)}
      {this.render_nav(2)}
      {this.render_subnav(2,0)}
      {this.render_subnav(2,1)}
      {this.render_nav(3)}
      {this.render_nav(4)}
