import { func } from "prop-types";
import React, { useState } from "react";
import ReactDOM from "react-dom";
import './buildingform.css';
import './form.css'
import checkbox from './checkbox.js'
import input from './input.js'
import choice from './dropdown'
import BuildingList from "./buildinglist";

export default function BuildingForm (){
  const [isSubmitted, setIsSubmitted] = useState(false);
  let province = 
  [
    {value: 1, text: "Hà Nội"},
    {value: 2, text: "Thành phố Hồ Chí Minh"},
    {value: 3, text: "Đà Nẵng"}
  ]
  let district = 
  [
    {value: 1, text: "1"},
    {value: 2, text: "2"},
    {value: 3, text: "3"}
  ]
  let communte = 
  [
    {value: 1, text: "1"},
    {value: 2, text: "2"},
    {value: 3, text: "3"}
  ]
  let floor = 
  [
    {value: 1, text: "1"},
    {value: 2, text: "2"},
    {value: 3, text: "3"}
  ]
  let budget = [
    [
      {value: "electric", text: "Điện"},
      {value: "wifi", text: "wifi"}
    ],
    [
      {value: "water", text: "Nước"},
      {value: "park", text: "Giữ xe"}
    ]
  ]
  const handleClick = (event) => {
    event.preventDefault();
    return(
      <BuildingList />
    )
  }
  const form = (
    <div>
      <span className="add-building-screen-text textxlsemibold">Thêm toà nhà</span>
      <form onSubmit={handleClick} className="add-building-screen-list">
        <div className="add-building-screen-name">
          {input(2,"Tên","Tên toà nhà")}
        </div>
        <div className="floor-area">
          {choice("Số tầng",floor)}
        </div>
        <div className="add-building-screen-service">
          <span className="form-subtitle textmdsemibold">Dịch vụ</span>
          <div className="checkbox-area">
            {budget.map((x) => <div>{x.map((y)=> checkbox("budget",y.value,y.text))}</div>)}
          </div>
        </div>
        <div className="add-building-screen-row1">
          <span className="form-subtitle textmdsemibold">Mô tả</span>
          <input
            type="text"
            placeholder="Mô tả"
            className="form-input"
          />
        </div>
        <div className="province-name">
          {choice("Tỉnh/Thành",province)}
        </div>
        <div className="add-building-screen-row">
          <div className="add-building-screen-floors">
            {choice("Quận/Huyện",district)}
          </div>
          <div className="add-building-screen-floors" style={{left: 267}}>
            {choice("Phường/Xã",communte)}
          </div>
        </div>
        <div className="num-street-name">
          {input(2,"Tên","Số, đường")}
        </div>
        <div className="add-building-screen-row2">
          <span className="form-subtitle textmdsemibold">Thêm ảnh</span>
            <input type="file" name="myfile" id="myfile" hidden/>
            <label for="myfile" className="add-building-screen-button">Nhấn để thêm ảnh</label>
        </div>
        <button type="submit" className="add-building-screen-small-secondary-button">
          <span className="add-building-screen-text28 textsmsemibold">Thêm toà nhà</span>
        </button>
      </form>
    </div>
  )
  return (
    <div>{form}</div>
  )
}
