import React,{ useEffect, useState } from 'react';
import axios from 'axios';
import ReactDOM from "react-dom";
import {Routes, Route, Link, useNavigate} from 'react-router-dom'
import '../style/form.css'
import '../style/list.css'
import {input,choice} from "../style/JSfunc";
import VoucherList from "./VoucherList";

let voucherID = 0;

export const setVoucherID = (id) => 
{
  voucherID = id
}

const colname = 
[
  {width: "8%", title: ""},
  {width: "16%", title: "Mã căn hộ"},
  {width: "20%", title: "Tên căn hộ"},
  {width: "12%", title: "Số phòng"},
  {width: "12%", title: "Diện tích"},
  {width: "14%", title: "Trạng thái"},
  {width: "12%", title: "Chi tiết"},
]

const customertype = [
  {code: 0, name: "Tất cả khách hàng"},
  {code: 1, name: "Khách hàng mới"},
]

const apartmenttype = [
  {code: 0, name: "Tất cả các căn hộ"},
  {code: 1, name: "Một vài căn hộ"},
]

function Main (){
  const [voucher, loadVoucher]= useState();
  useEffect( ()=>{
    loadItem('apartments', voucherID, loadVoucher);
  },[voucherID]);
  const navigate = useNavigate();
  const [apartments, setApartments] = useState([]);
  
  useEffect(() => {
    axios
      .get('http://localhost:1337/api/apartments')
      .then(({ data }) => setApartments(data.data))
  }, [])
  const handleClick = (event) => {
    event.preventDefault();
    navigate('Voucher/VoucherList');
  }
    return(
      <div>
        <span className="function-title textxlsemibold">Thêm mã giảm giá</span>
        <form onSubmit={handleClick} className="main-zone">
          <div className="big-row">
            <div className="col-left">
              <div className="item-area">
                {input(1,"Tên mã","vouchername",true,"Tên",null,null,1,50,"")}
              </div>
              <div className="item-area double-col">
                {input(2,"Ngày bắt đầu","startday",true,"",null,null,"","","")}
                {input(3,"Thời gian bắt đầu","starttime",true,"",null,null,"","","")}
              </div>
              <div className="item-area double-col">
                {input(2,"Ngày kết thúc","endday",true,"",null,null,"","","")}
                {input(3,"Thời gian kết thúc","endtime",true,"",null,null,"","","")}
              </div>
            </div>
            <div className="col-left">
              <div className="item-area">
                {choice("Áp dụng cho","apply-customer",customertype)}
              </div>
              <div className="item-area double-col">
                {input(0,"quantity","Số lượng",true,"Số lượng",null,null,0,100,1)}<div/>
              </div>
              <div className="item-area double-col">
                {input(0,"Phần trăm giảm","percent_off",false,"Phần trăm",null,null,0,100,1)}
                {input(0,"Giảm tối đa","max_off",false,"Tối đa",null,null,0,null,1)}
              </div>
            </div>
          </div>
          <div className="main-head">
            {choice("Các căn hộ áp dụng","apply-apartment",apartmenttype)}
          </div>
          <div className="table-area">
            <table>
              <tr className="rowtitle">
                {colname.map((a)=><th className="textsm" style={{width: a.width}}>{a.title}</th>)}
              </tr>
              {apartments.map(data => 
              <tr>
                <td><div className="checkbox-item">
                  <input type="checkbox" className="checkbox" name="apartments" value={data.id}/>
                </div></td>
                <td className="textsm">{data.id}</td>
                <td className="textsm">{data.attributes.Apartment_Name}</td>
                <td className="textsm">{data.attributes.Livingroom + data.attributes.Bedroom + data.attributes.Kitchen + data.attributes.Restroom}</td>
                <td className="textsm">{data.attributes.Size}</td>
                <td className="textsm">{}</td>
                <td className="textsm"><Link to='/BuildingDetail'>Xem chi tiết</Link></td>
              </tr>)}
            </table>
          </div>
          <div className="submit-area">
            <button type="submit" className="submit-button">
              <span className="submit-btntxt textsmsemibold">Thêm nhân viên</span>
            </button>
          </div>
        </form>
      </div> 
    )
}

export default function VoucherForm ()
{
  return(
    <div>
    <Routes>
      <Route path="/" element={<Main />} />
      <Route path="/Voucher/VoucherList" element={<VoucherList />} />
    </Routes>
    </div>
  )
}
