function getProvince ()
{
  const [p1, setP1] = useState([]);
  useEffect(() => {
    fetch('https://provinces.open-api.vn/api/p/')
      .then((response) => response.json())
      .then((data) => setP1(data));
  }, []);
  return p1
}
function getDistrict (province_name)
{
  const [p2, setP2] = useState([]);
  useEffect(() => {
    fetch(`https://provinces.open-api.vn/api/p/${province_name}?depth=2`)
      .then((response) => response.json())
      .then((data) => setP2(data));
  }, []);
  return p2
}
