import { func } from "prop-types";
import React, { useState } from "react";
import ReactDOM from "react-dom";
import {Routes, Route, Link, useNavigate} from 'react-router-dom';
import './main.css';
import './list.css';
import BuildingForm from "./BuildingForm";
import BuildingDetail from "./BuildingDetail";

function Main () {
  const navigate = useNavigate();
  const colname = 
  [
    {width: "14%", title: "Mã tòa nhà"},
    {width: "20%", title: "Tên tòa nhà"},
    {width: "12%", title: "Số tầng"},
    {width: "22%", title: "Địa chỉ"},
    {width: "12%", title: "Chi tiết"},
    {width: "12%", title: "Cập nhật / Xoá"}
  ]
  const tbcontent = 
  [
  [
    "BU-001",
    "Toà nhà A",
    "5",
    "Quận 1",
    <Link to='/BuildingDetail'>Xem chi tiết</Link>,
    <><img alt="edit4140" src="/icon/edit-icon.svg" className="edit-icon" />
      <img alt="trashalt4140" src="/icon/delete-icon.svg" className="trash-icon" /></>
  ],
  [
    "BU-001",
    "Toà nhà A",
    "5",
    "Quận 1",
    <Link to='/BuildingDetail'>Xem chi tiết</Link>,
    <><img alt="edit4140" src="/icon/edit-icon.svg" className="edit-icon" />
      <img alt="trashalt4140" src="/icon/delete-icon.svg" className="trash-icon" /></>
  ]
  ]
  const handleClick = (event) =>
  {
    event.preventDefault();
    navigate('/BuildingForm');
  }
  const rows = () =>
  {
    return(
    <tr>
      {tbcontent.map((a)=><td className="textsm">{a}</td>)}
    </tr>
    )
  }
  return(
    <div>
      <span class="function-title textxlsemibold">Toà nhà</span>
      <div class="main-zone">
        <div class="main-head">
          <div class="main-head-center">
              <input
                type="text"
                placeholder="Tìm kiếm"
                className="searchbar"
              />
              <img
                alt="searchI414"
                src="/icon/search-icon.svg"
                className="search-icon"
              /> 
            <button class="addbutton" onClick={handleClick}>
              <span class="addbutton-text textsmsemibold">Thêm toà nhà</span>
              <img
                src="./icon/add-icon.svg"
                class="pluscircle"
              />
            </button>
          </div>
        </div>
        <table>
          <tr className="rowtitle">
            {colname.map((a)=><th className="textsm" style={{width: a.width}}>{a.title}</th>)}
          </tr>
            {tbcontent.map((a)=><tr>{a.map((b)=><td className="textsm">{b}</td>)}</tr>)}
        </table>
      </div>
    </div>
  )
}

export default function BuildingList ()
{
  return(
    <div>
    <Routes>
      <Route path="/" element={<Main />} />
      <Route path="/BuildingForm" element={<BuildingForm />} />
      <Route path="/BuildingDetail" element={<BuildingDetail />} />
    </Routes>
    </div>
  )
}
