import axios from 'axios';
import React,{ useEffect, useState } from 'react';
import ReactDOM from "react-dom";
import {Routes, Route, Link, useNavigate} from 'react-router-dom';
import './main.css';
import './list.css';
import table from "./table";
import BuildingForm from "./BuildingForm";
import BuildingDetail from "./BuildingDetail";

const colname = 
[
  {width: "14%", title: "Mã tòa nhà"},
  {width: "20%", title: "Tên tòa nhà"},
  {width: "10%", title: "Số tầng"},
  {width: "24%", title: "Địa chỉ"},
  {width: "14%", title: "Chi tiết"},
  {width: "14%", title: "Cập nhật / Xoá"}
]
// const tbcontent = 
// [[
//   "BU-001",
//   "Toà nhà A",
//   "5",
//   "Quận 1",
//   <Link to='/BuildingDetail'>Xem chi tiết</Link>,
//   <><img alt="edit4140" src="/icon/edit-icon.svg" className="edit-icon" />
//     <img alt="trashalt4140" src="/icon/delete-icon.svg" className="trash-icon" /></>
// ],
// [
//   "BU-001",
//   "Toà nhà A",
//   "5",
//   "Quận 1",
//   <Link to='/BuildingDetail'>Xem chi tiết</Link>,
//   <><img alt="edit4140" src="/icon/edit-icon.svg" className="edit-icon" />
//     <img alt="trashalt4140" src="/icon/delete-icon.svg" className="trash-icon" /></>
// ]]

function Main () {
  const [error, setError] = useState(null);
  const [buildings, setBuildings] = useState([]);
  useEffect(() => {
    axios
      .get('http://localhost:1337/api/buildngs')
      .then(({ data }) => setBuildings(data))
      .catch((error) => setError(error))
  }, [])
  const navigate = useNavigate();
  const handleClick = (event) =>
  {
    event.preventDefault();
    navigate('/BuildingForm');
  }
  return(
    <div>
      <span class="function-title textxlsemibold">Toà nhà</span>
      <div class="main-zone">
        <div class="main-head">
          <div class="search-list">
            <input
              type="text"
              placeholder="Tìm kiếm"
              className="searchbar"
            />
            <img
              src="/icon/search-icon.svg"
              className="search-icon"
            /> 
          </div>
          <button class="addbutton" onClick={handleClick}>
              <span class="addbutton-text textsmsemibold">Thêm toà nhà</span>
              <img
                src="./icon/add-icon.svg"
                class="pluscircle"
              />
            </button>
        </div>
        <div className="table-area">{table(colname, buildings)}</div>
      </div>
    </div>
  )
}

export default function BuildingList ()
{
  return(
    <div>
    <Routes>
      <Route path="/" element={<Main />} />
      <Route path="/BuildingForm" element={<BuildingForm />} />
      <Route path="/BuildingDetail" element={<BuildingDetail />} />
    </Routes>
    </div>
  )
}
