import React, { useState } from "react";
import {Routes, Route, Link} from 'react-router-dom'
import './main.css';
import BuildingList from "./BuildingList";
import ApartmentList from './ApartmentList';
import ContractList from './ContractList';
import CustomerList from './CustomerList';
import VoucherList from './VoucherList';
import EmployeeList from './EmployeeList';
import CheckAttendanceList from './CheckAttendanceList';
import QRCode from './QRcode'
import BuildingForm from './BuildingForm';
import ApartmentForm from "./ApartmentForm";
import VoucherForm from './VoucherForm';
import EmployeeForm from "./EmployeeForm";
import BuildingDetail from "./BuildingDetail";

const num = [1,2,3,4,5,6,7,8,9]

const [navigator, setNavigator] = useState(
  {nav: [
    {name: "Thống kê", expand: 0, select: 0, link: ""},
    {name: "Tòa nhà", expand: 0, select: 0, link: "buildinglist"},
    {name: "Căn hộ", expand: 1, select: 0, link: "apartmentlist",
      subnav:
      [
        {name: "Một hợp đồng", select: 1, link: "apartmentlist"},
        {name: "Nhiều hợp đồng", select: 0, link: "apartmentlist"},
      ],
      cur: 0  
    },
    {name: "Hợp đồng", expand: 0, select: 0, link: "contractlist"},
    {name: "Hóa đơn", expand: 0, select: 0, link: ""},
    {name: "Mã giảm giá", expand: 0, select: 0, link: "voucherlist"},
    {name: "Khách hàng", expand: 0, select: 0, link: "customerlist"},
    {name: "Nhân viên", expand: 0, select: 0, link: "employeelist"},
    {name: "Yêu cầu", expand: 0, select: 0, link: ""},
    {name: "Điểm danh nhân viên", expand: 1, select: 0, link: "QRcode",
      subnav:
      [
        {name: "Điểm danh", select: 1, link: "QRcode"},
        {name: "Danh sách điểm danh", select: 0, link: "checkattendancelist"},
      ],
      cur: 0  
    },
  ],
  pre: 0}
)

const Profile = () =>
{
  return (
  <div>
    <div className="buildinglist-screen-info">
      <img src="/playground_assets/avatari414-gulf.svg"
        className="buildinglist-screen-avatar"
      />
      <span className="buildinglist-screen-text02 textmdsemibold">Nguyen Van B</span>
      <span className="buildinglist-screen-text04 textsmsemibold">Admin</span>
    </div>
    <img
      alt="Line13I414"
      src="/playground_assets/line13i414-hri4.svg"
      className="buildinglist-screen-line13"
    />
  </div>
  )
}

function Left_logo ()
{
    return (
      <div>
          <div className="buildinglist-screen-logo">
            <span className="buildinglist-screen-text06 displaysmsemibold">Appname</span>
            <img
              alt="Line12I414"
              src="/playground_assets/line12i414-v2in.svg"
              className="buildinglist-screen-line12"
            />
          </div>
          <div className="buildinglist-screen-profile1">
          <Profile />
          </div>
      </div>
    )
}

function Sub_Nav (props){
  if (props.parentselect==1)
  return (
    <Link to={props.link}>
    <div className="nav-elmt-bg" onClick={props.onClick}>
