import React from 'react';
import './style/main.css';


export default function Popup (p) {
    return (p.trigger) ? (
      <><div class="delete-blk" onClick={()=>p.setTrigger(false)}></div>
      <div class="delete-bg">
        <div class="delete-title">
          Bạn có muốn xóa {p.category} {p.id} ?
        </div>
        <div class="delete-subtitle">
          Tên {p.category} : {p.name} <br/>
          {p.attr2} : {p.value2} <br/>
        </div>
        <button onClick={()=>p.setDelete(p.id)} className="delete-button">Xóa</button>
        <button onClick={()=>p.setTrigger(false)} className="cancel-button">Hủy</button>
      </div></>
    ) : ""
}
