import { func } from "prop-types";
import React, { useState } from "react";
import {Routes, Route, Link, useNavigate} from 'react-router-dom';
import './EmployeeForm.css';
import './form.css';
import input from './input';
import choice from "./dropdown";
import EmployeeList from "./EmployeeList";

const role = [
    {value: 1, text: "Sửa chữa"},
    {value: 2, text: "Vệ sinh"},
    {value: 3, text: "Sửa chữa"}
]
const position = [
    {value: 1, text: "Sửa chữa"},
    {value: 2, text: "Vệ sinh"},
    {value: 3, text: "Sửa chữa"}
]
const workday =
[
    {value: 1, text: "Thứ hai"},
    {value: 2, text: "Thứ ba"},
    {value: 3, text: "Thứ tư"},
    {value: 4, text: "Thứ năm"},
    {value: 5, text: "Thứ sáu"},
    {value: 6, text: "Thứ bảy"},
    {value: 7, text: "Chủ nhật"}
]
function Main (){
  const navigate = useNavigate();
  const handleClick = (event) => {
    event.preventDefault();
    navigate('/EmployeeList');
  }
  return(
    <div>
      <span className="add-employee-screen-text textxlsemibold">Thêm nhân viên</span>
      <form onSubmit={handleClick} className="main-zone">
          <div className="item-area double-col">
            {input(2,"surname","Tên","Họ lót")}
            {input(2,"firstname","Tên","Tên")}     
          </div> 
          <div className="item-area double-col">
            {input(2,"email","Email","Email")}  
            {input(2,"password","Mật khẩu đăng nhập lần đầu","Mật khẩu")}
          </div>
          <div className="item-area double-col">
            <span className="form-subtitle textmdsemibold">Giới tính</span>
            <div className="add-employee-screen-radiobutton">
                <input type="radio" id="1" name="gender" value="male"/>
                <label for="1">Nam</label>
                <input type="radio" id="2" name="gender" value="female"/>
                <label for="2">Nữ</label>
                <input type="radio" id="3" name="gender" value="other"/>
                <label for="2">Khác</label>
            </div>
            <div/>
            {input(3,"","","Ngày sinh")}
          </div>
        <div className="item-area">
        <div className="col-left">
          <div className="item-area">
            <span className="form-subtitle textmdsemibold">Công việc</span>
            <div className="item-area double-col">
                {input(1,"salary","Lương", "Lương")}
              <span className="add-employee-screen-text09 textmd">triệu đồng</span>
            </div>
            <div className="item-area double-col">
                {input(1,"welfare","Phúc lợi","Phúc lợi")}
              <span className="add-employee-screen-text09 textmd">triệu đồng</span>
            </div>
            <div className="item-area double-col">
              {choice("Chức vụ",role)}
              {choice("Vị trí",position)}
            </div>
          </div>
        </div>
        <div className="col-right">
        <span className="form-subtitle textmdsemibold">Thời gian làm việc</span>
        <div className="item-area double-col">
            {choice("Từ",workday)}
            {choice("Đến",workday)}
        </div>
        <div className="item-area double-col">
          {input(1,"offday","Ngày nghỉ","Ngày nghỉ tối đa")}<div/>
        </div>
        <div className="item-area">
            <span className="form-subtitle textmdsemibold">Ca làm việc</span>
            <div className="add-employee-screen-radiobutton2">
              <input type="radio" id="1" name="shift" value="1"/>
              <label for="1">Ca 1</label>
              <input type="radio" id="2" name="shift" value="2"/>
              <label for="2">Ca 2</label>
              <input type="radio" id="3" name="shift" value="3"/>
              <label for="2">Ca 3</label>
            </div>
        </div>
        </div>
        </div>
        <button type="submit" className="add-employee-screen-small-secondary-button">
          <span className="add-employee-screen-text62 textsmsemibold">Thêm nhân viên</span>
        </button>
      </form>
    </div>
  )
}

export default function EmployeeForm ()
{
  return(
    <div>
    <Routes>
      <Route path="/" element={<Main />} />
      <Route path="/EmployeeList" element={<EmployeeList />} />
    </Routes>
    </div>
  )
}
