import { func } from "prop-types";
import React, { useState } from "react";
import {Routes, Route, Link} from 'react-router-dom'
import './main.css';
import BuildingList from './buildinglist.js';
import ApartmentList from './apartmentlist.js';
import ContractList from './contractlist.js';
import CustomerList from './customerlist.js';
import VoucherList from './voucherlist.js';
import EmployeeList from './employeelist.js';
import CheckAttendanceList from './checkattendancelist.js';
import CreateQRScreen from './QRcode.js';
import BuildingForm from './buildingform.js';
import ApartmentForm from './apartmentform.js';
import VoucherForm from './voucherform.js';
import EmployeeForm from "./employeeform";
import BuildingDetail from './buildingdetail.js';

const num = [1,2,3,4,5,6,7,8,9]

const Profile = () =>
{
  return (
  <div>
    <div className="buildinglist-screen-info">
      <img src="/playground_assets/avatari414-gulf.svg"
        className="buildinglist-screen-avatar"
      />
      <span className="buildinglist-screen-text02 textmdsemibold">Nguyen Van B</span>
      <span className="buildinglist-screen-text04 textsmsemibold">Admin</span>
    </div>
    <img
      alt="Line13I414"
      src="/playground_assets/line13i414-hri4.svg"
      className="buildinglist-screen-line13"
    />
  </div>
  )
}

function Left_logo ()
{
    return (
      <div>
          <div className="buildinglist-screen-logo">
            <span className="buildinglist-screen-text06 displaysmsemibold">Appname</span>
            <img
              alt="Line12I414"
              src="/playground_assets/line12i414-v2in.svg"
              className="buildinglist-screen-line12"
            />
          </div>
          <div className="buildinglist-screen-profile1">
          <Profile />
          </div>
      </div>
    )
}

function Sub_Nav (props){
  if (props.parentselect==1)
  return (
    <Link to={props.link}>
    <div className="nav-elmt-bg" onClick={props.onClick}>
      <span className="nav-elmt-text" style={{color: props.select ? "rgba(46, 144, 250, 1)" : ""}}>{props.name}</span>
      <img
        src={`/Nav-Logo/${props.name}.svg`}
        className="nav-elmt-img"
        style={{fill: props.select ? "rgba(46, 144, 250, 1)" : ""}}
      />
    </div>
    </Link>
  );
}

function Nav_Elmt (props) {
  const expand = (i) =>
  {
    if (i==1)
    return(
      <img
      src="/playground_assets/anglelefti414-d6w7.svg"
      className="angleleft"
      />
    )
  }
  return (
    <Link to={props.link}>
    <div className="nav-elmt-bg" onClick={props.onClick}>
      <span className="nav-elmt-text" style={{color: props.select ? "rgba(46, 144, 250, 1)" : ""}}>{props.name}</span>
      <img
        src={`/Nav-Logo/${props.name}.svg`}
        className="nav-elmt-img"
        style={{fill: props.select ? "rgba(46, 144, 250, 1)" : ""}}
      />
      {expand(props.expand)}
    </div>
    </Link>
  );
}

