class Navigator extends React.Component
{
  constructor(props) {
    super(props);
    this.state = {
        nav: [
          {name: "Thống kê", expand: 0, select: 0, link: "/"},
          {name: "Tòa nhà", expand: 0, select: 0, link: "/buildinglist"},
          {name: "Căn hộ", expand: 1, select: 0, link: "/apartmentlist",
            subnav:
            [
              {name: "Một hợp đồng", select: 1, link: "/apartmentlist"},
              {name: "Nhiều hợp đồng", select: 0, link: "/apartmentlist"},
            ],
            cur: 0  
          },
          {name: "Hợp đồng", expand: 0, select: 0, link: "/contractlist"},
          {name: "Hóa đơn", expand: 0, select: 0, link: "/"},
          {name: "Mã giảm giá", expand: 0, select: 0, link: "/voucherlist"},
          {name: "Khách hàng", expand: 0, select: 0, link: "/customerlist"},
          {name: "Nhân viên", expand: 0, select: 0, link: "/employeelist"},
          {name: "Yêu cầu", expand: 0, select: 0, link: "/"},
          {name: "Điểm danh nhân viên", expand: 1, select: 0, link: "/QRcode",
            subnav:
            [
              {name: "Điểm danh", select: 1, link: "/QRcode"},
              {name: "Danh sách điểm danh", select: 0, link: "/checkattendancelist"},
            ],
            cur: 0  
          },
        ],
        pre: 0
    };
  }

  handleClick(i) {
    const nav = this.state.nav.slice();
    nav[i].select = 1;
    nav[this.state.pre].select = 0;
    this.setState({nav: nav, pre: i});
  }

  handleClicksub(i,j) {
    const nav = this.state.nav.slice();
    nav[i].subnav[j].select = 1;
    nav[i].subnav[1-j].select = 0;
    nav[i].cur = j
    this.setState({nav: nav});
  }
  subnav(i,j)
  {
    return(
      <Sub_Nav
        parentselect={this.state.nav[i].select}
        select={this.state.nav[i].subnav[j].select}
        name={this.state.nav[i].subnav[j].name}
        link={this.state.nav[i].subnav[j].link}
        onClick={() => this.handleClicksub(i,j)}
      />
    )
  }
  parnav(i)
  {
    return(
      <Nav_Elmt
        select={this.state.nav[i].select}
        name={this.state.nav[i].name}
        expand={this.state.nav[i].expand}
        link={this.state.nav[i].link}
        onClick={() => this.handleClick(i)}
      />
    )
  }
  render_nav(i)
  {
    if (this.state.nav[i].expand==0)
    return this.parnav(i)
    else return(
      <div>
      {this.parnav(i)}
      {this.subnav(i,0)}
      {this.subnav(i,1)}
      </div>
    )
  }
  render()
  {
    return (
    <div>
      {Left_logo()}
      <div className="thongke">{this.render_nav(0)}</div>      
      <span className="buildinglist-screen-text14 textmdsemibold">Quản lý</span>
      <div className="navigator-list">
        {num.map((a) => this.render_nav(a) )}
      </div>
    </div>
    )
  }
}

